import React, { useMemo } from "react";
import "./Student.css";

const Student = (props) => {
  const {
    city,
    company,
    email,
    firstName,
    grades,
    id,
    lastName,
    pic,
    skill,
  } = props.info;

  const average = useMemo(() => {
    let average = 0;
    for (const grade of grades) {
      average += parseInt(grade, 10);
    }
    return average / grades.length;
  }, [grades]);

  console.log(props.info);
  return (
    <div style={{ width: "100%", display: "flex" }}>
      <div
        style={{
          justifyContent: "center",
          alignItems: "center",
          flex: 1,
        }}
      >
        <img src={pic} className="profile-pic" />
      </div>
      <div
        style={{
          justifyContent: "flex-start",
          alignItems: "left",
          display: "block",
        }}
      >
        <p className="name">
          {firstName} {lastName}
        </p>
        <p>Email: {email}</p>
        <p>Company: {company}</p>
        <p>Skill: {skill}</p>
        <p>Average: {average}%</p>
      </div>
    </div>
  );
};

export default Student;
